/**
 * @ngdoc directives
 * @name addressAutoComplete
 * @memberof nrcomInspinia
 * @description
 * Directive addressAutoComplete
 *
 * @example <caption>integration html</caption>
 *          <addressAutoComplete></addressAutoComplete>
 *
 */
angular.module("nrcomInspinia")
.directive("addressAutoComplete", [ function() {
    return {
        link: function(scope,element){
            var input = element.children().first()[0],
                autocomplete = new google.maps.places.Autocomplete(input);

            var mappingAddress = function(components, classical){
                var streetNumber = components.find(function(elem){
                        return elem.types[0] === "street_number";
                    }),
                    streetName = components.find(function(elem){
                        return elem.types[0] === "route";
                    }),
                    city = components.find(function(elem){
                        return elem.types[0] === "locality";
                    }),
                    zipCode = components.find(function(elem){
                        return elem.types[0] === "postal_code";
                    }),
                    country = components.find(function(elem){
                        return elem.types[0] === "country";
                    });
                classical.streetNumber = streetNumber ? streetNumber["long_name"] : "";
                classical.streetName = streetName ? streetName["long_name"] : "";
                classical.city = city ? city["long_name"] : "";
                classical.zipCode = zipCode ? parseInt(zipCode["long_name"]) : "";
                classical.country = country ? country["long_name"] : "";
            }

             var fillAddress = function(place){
                var components = place["address_components"],
                    classical = scope.address.classical,
                    geoloc;
                if(angular.isUndefined(scope.address.geolocation)){
                    scope.address.geolocation = {
                        latitude: "",
                        longitude: ""
                    };
                }
                geoloc = scope.address.geolocation;

                mappingAddress(components, classical);

                geoloc.latitude = place.geometry.location.lat();
                geoloc.longitude = place.geometry.location.lng();
            };

            google.maps.event.addListener(autocomplete, "place_changed", function(){
                var place = autocomplete.getPlace();

                fillAddress(place);
                scope.$apply();
            });
        },
        scope: {
            name : "@",
            address : "=",
            value : "="
        },
        templateUrl: "./directives/forms/addressAutoComplete/addressAutoComplete.html",
        restrict: "EA"
    };
}]);

