/**
 * @ngdoc directives
 * @name formItem
 * @memberof nrcomInspinia
 * @description
 * Directive formItem
 *
 * @example <caption>integration html</caption>
 *          <form-item></form-item>
 *
 */
angular.module("nrcomInspinia")
.directive("formItem", [
    "validateSiret","$http","$q",
    function(validateSiret,$http,$q) {
    return {
        require: "^form",
        link: function(scope, element, attrs, formCtrl){


            var item = scope.item,
                model = scope.model;

            if((item.type == "date" || item.type == "time") && model[item.name]){
                model[item.name] = new Date(model[item.name]);
            }

            scope.refreshItems = function(search){
                item.loading = true;
                var promise;
                if(item.refreshUrl){
                    promise =
                    $http(
                        {
                            method: 'GET',
                            url: item.refreshUrl,
                            params : {
                                query: "*:*" + search + "*"
                            }
                        }
                    ).then(function(data){
                        return $q.resolve(data.data.data);
                    });
                }
                if(!item.refreshService && item.refreshItems ){
                    item.refreshItems(search);
                    item.loading = false;
                }
                else if(item.refreshService){
                    promise = item.refreshService.search(false, {
                        query: "*:*" + search + "*"
                    });
                }
                if(!angular.isUndefined(promise)){
                    promise.then(function(data){
                        item.loading = false;
                        item.items = data;
                    });
                }
            };

            /**
             * Méthode de vérification de item.max dans le cas
             * d'un type autocompleteChoice. Permet d'afficher
             * soit une sélection multiple, soit une simple
             *
             * @method checkItemsChoice
             * @return {Boolean}
             */
            scope.checkItemsChoice = function(){
                if(angular.isUndefined(item.max)){
                    return false;
                }
                else{
                    return item.max > 1;
                }
            }

            scope.isPresent = function(){
                var idProperty = "_id";

                if(item.valueField){
                    idProperty = item.valueField;
                }

                var modelData = angular.isObject(model[item.name]) ? model[item.name][idProperty] : model[item.name];

                return item.items.find(function(elem){

                    if( elem[idProperty]  ===  modelData){
                        if( angular.isObject(model[item.name]) ){
                            model[item.name] = elem;
                        }else{
                            model[item.name] = elem[idProperty];
                        }
                    }
                    return model[item.name][idProperty] === elem[idProperty];
                });
            };

            /**
             * Permet de render le message d'erreur de la vérification min-max
             * pour un champ number
             * @method
             * @param  {Object} item l'item du formulaire
             * @return {string}      la chaine
             */
            scope.renderMinMaxNum = function(item){
                res = item.label+" doit etre ";
                if(item.min && item.max ){
                    res +="compris entre "+item.min+" et "+item.max;
                }else if (item.min) {
                    res +="superieur a "+item.min;

                }else {
                    res +="inferieur a "+item.max;
                }
                return res;
            };

            /**
             * Permet de render le message d'erreur de la vérification min-max
             * pour un champ string
             * @method
             * @param  {Object} item l'item du formulaire
             * @return {string}      la chaine
             */
            scope.renderMinMaxStr = function(item){
                res = item.label+" doit faire ";
                if(item.min && item.max ){
                    res +="compris entre "+item.min+" et "+item.max;
                }else if (item.min) {
                    res +="au moins a "+item.min;
                }else {
                    res +="maximum a "+item.max;
                }
                return res+=" caracteres";
            };

            /**
             * Permet d'ajouter un sous formulaire
             * @method addSubForm
             */
            scope.addSubForm = function(){
                item.num++;
                model[item.name].push({});
            };

            /**
             * Permet de supprimer le dernier sous formulaire
             * ajouté précédemment
             * @method addSubForm
             */
            scope.removeSubForm = function(index){
                item.num--;
                model[item.name].splice(index,1);
            };

            /**
             * Permet l'affichage du titre du sub form
             * passé en paramètre (titre + index)
             * @param  {Object} item Element
             * @return {String}      Titre du sub form
             */
            scope.renderTitleSubForm = function(item){
                var index = scope.index + 1;
                return item.title + " " + index;
            };


            // Inclusion du formulaire courrant pour la gestion des erreurs
            scope.form = formCtrl;

            // Mapping entre le type d'item et le type d'input html
            scope.map = {
                string : "text",
                number : "number",
                email : "email",
                date : "date",
                time : "time",
                password : "password"
            };

            scope.blur = function($event){
                if(item.readonly || scope.readonly){
                    $event.target.blur();
                }
            };

            // Paters et messages d'erreurs des vtype non-email
            scope.mapPaterns = {
                phone : {
                    patern : "(0|\\+33)[1-9][0-9]{8}",
                    message : "Le numéro de téléphone est invalide"
                },
                zipcode : {
                    patern : "([A-Z]+[A-Z]?\-)?[0-9]{2} ?[0-9]{3}",
                    message : "Le code postal est invalide"
                },
                siret : {
                    patern : validateSiret,
                    message : "Le SIRET est invalide"
                }
            };

            // Pour eviter les undefind lors des subform, on crée un objet quand
            // il y a min = max = 1 ( sous objet )
            // sinon une liste avec 1 objet vide ( liste de sous objet )
            if(item.type === "subform" ){

                if( !model[item.name] ){

                    if(!item.num){
                        item.num = item.min
                    }
                    if( !item.array && ( item.min === item.max && item.max === 1 ) ){
                        model[item.name] = {};
                    }else{
                        model[item.name] = [];
                        for (var i = 0; i < item.num; i++) {
                            model[item.name].push({});
                        }
                    }

                }else if(!model[item.name][0]){
                    model[item.name][0] = {};
                }
            };

            scope.displaySelect = function(value){
                if(angular.isUndefined(value) || value === null){
                    value = "";
                }
                if( !item.displayField ){
                    return value;
                }else if( angular.isFunction(item.displayField) ){
                    return item.displayField(value);
                }else if(angular.isArray(value)){
                    for(var el in value){
                        if(angular.isObject(value[el])){
                            value[el] = value[el][item.displayField];
                        }
                    }
                    return value;
                }else{
                    return value[item.displayField];
                }
            };

            scope.getValueSelected = function(value){
                if(angular.isUndefined(value)){
                    value = null;
                }
                if( !item.valueField ){
                    return value;
                }else if( angular.isFunction(item.valueField) ){
                    return item.valueField(value);
                }else{
                    return value[item.valueField];
                }
            };

            scope.renderReadOnly = function(model){
                if( model === true ) {
                    return "Oui";
                }else if( model === false ) {
                    return "Non";
                }else if( angular.isString(model) ){
                    return model;
                }else {
                    return  scope.displaySelect(model);
                }
            };

        },
        scope: {
            item:"=",
            model:"=",
            path:"@",
            index: "=",
            readonly:"="
        },
        templateUrl: "./directives/forms/formItem/formItem.html",
        restrict: "EA"
    };
}]);


angular.module("nrcomInspinia")
.service('validateSiret',[function(){
    var nCheck = 0,
    nDigit = 0,
    bEven = false,
    valid;

    return {
        test: function(value){
            if (!/^[0-9]{14}/.test(value)){
                return false;
            }

            for (var n = value.length - 1; n >= 0; n--) {
                var cDigit = value.charAt(n),
                nDigit = parseInt(cDigit, 10);
                if (bEven) {
                    if ((nDigit *= 2) > 9){
                        nDigit -= 9;
                    }
                }
                nCheck += nDigit;
                bEven = !bEven;
            }

            valid = (nCheck % 10) === 0;
            nCheck = 0;
            nDigit = 0;
            bEven = 0;

            return valid;
        }
    }
}]);


