angular.module("nrcomInspinia")
.directive("imageManager", [ "$timeout", "keycloak", function($timeout,keycloak) {


    return {

        link: function(scope){

            /**
             * Callback a l'ajout de fichier
             * @param  {[type]} uploader pluplaod
             * @param  {[type]} files    les fichiers ajouté
             */
            var filesAddedCallback=function(uploader,files){

                scope.uploader.share.ising = true;

                for(var index in files) {
                    var file = files[index];
                    scope.uploader.share.files.push({
                        label: file.name,
                        percent: file.percent,
                        tempId: file.id,
                        src: "",
                        format:"",
                        inProgress: true
                    });
                }

                if(scope.uploader.callback && scope.uploader.callback.filesAdded){
                    scope.uploader.callback.filesAdded(uploader,files);
                }
                $timeout(function() {
                    uploader.setOption("headers", {
                        Authorization: "Bearer " + keycloak.token
                    });
                    uploader.start();
                }, 100);

            };

            /**
             * Callback a la progression d'un fichier
             * @param  {[type]} uploader pluplaod
             * @param  {[type]} file    le fichier qui progresse
             */
            var ProgressCallback= function(uploader, file) {
                // on recherche l'élément
                var conf = scope.uploader.share.files.find(function(item) {
                    return (item.tempId === file.id);
                });

                if(!angular.isUndefined(conf)) {
                    conf.percent = file.percent;
                    conf.inProgress = file.percent<100;
                }

                if(scope.uploader.callback && scope.uploader.callback.Progress){
                    scope.uploader.callback.Progress(uploader,file);
                }

            };

            /**
             * Callback a l'upload progression d'un fichier
             * @param  {[type]} uploader pluplaod
             * @param  {[type]} file    le fichier qui progresse
             * @param  {[type]} response response la réponse
             */
            var fileUploadedCallback= function(uploader, file, response) {

                response=me.transformResponse(response);

                var uplaoding = false;
                for (var scopefile in scope.uploader.share.files) {
                    if (scope.uploader.share.files.hasOwnProperty(scopefile)) {
                        if( scopefile.percent < 100 ){
                            uplaoding = true;
                        }
                    }
                }

                scope.uploader.share.uploaded = uplaoding;
                console.log(scope.uploader.callback);
                if(scope.uploader.callback && scope.uploader.callback.fileUploaded){
                    scope.uploader.callback.fileUploaded(uploader, file, response);
                }
            };

            /**
             * Callback en cas d'erreur
             * @param  {[type]} uploader pluplaod
             * @param  {[type]} error    l'erreur
             */
            var errorCallback= function(uploader, error) {
                if(scope.uploader.callback && scope.uploader.callback.error){
                    scope.uploader.callback.error(uploader, error);
                }
                console.error(error);
            };


            var me=this;

            me.transformResponse = function(response){
                var headers={},
                splited= response.responseHeaders.split(":");
                headers[splited[0].trim()] = splited[1].trim();
                var res={
                    headers:headers,
                    body:JSON.parse(response.response),
                    status:response.status
                };
                return res;
            };

            scope.getClass=function(img,images,$index){
                if(angular.isFunction(scope.options.imgClass)){
                    return scope.options.imgClass(img,images,$index);
                }
            };
            if(scope.uploader){

                var defaultOptions = {
                    "multi_selection": false,
                    "filters": {
                        "max_file_size": "8mb"
                    }
                };

                if(!scope.uploader.share){
                    scope.uploader.share = {};
                }
                scope.uploader.share.files = [];

                scope.uploader.share.ising = false;
                scope.uploader.share.uploaded = false;

                scope.url= scope.uploader.url;
                scope.uplaoderOptions= angular.merge({},defaultOptions,scope.uploader.options);

                scope.callbacks = {

                    filesAdded : filesAddedCallback,
                    Progress : ProgressCallback,
                    fileUploaded : fileUploadedCallback,
                    error : errorCallback

                };


            }
        },
        scope: {
            images : "=",
            options: "=",
            uploader: "="
        },
        templateUrl: "./directives/widgets/imageManager/imageManager.html",
        restrict: "E"
    };
}]);

