/**
 * @ngdoc directives
 * @name tabs
 * @memberof nrcomInspinia
 * @description
 * Directive tabs
 *
 * @example <caption>integration html</caption>
 *          <tabs></tabs>
 *
 */
angular.module("nrcomInspinia")
.directive("tabs", ["$state", function($state) {
    return {
        link: function(){
            var updateTabs = function() {
                scope.update_tabs();
            };
        },
        scope: {
            tabs: "=data",
            type: "@",
            justified: "@",
            vertical: "@",
            class: "@"
        },

        controller: ["$scope", function($scope) {

            if (!$scope.tabs) {
                throw new Error("Property tabs missing");
            }

            if (!angular.isArray($scope.tabs)) {
                throw new Error("Property tabs should be an array");
            }

            var currentStateEqualTo = function(tab) {
                var isEqual = $state.is(tab.route, tab.params, tab.options);
                return isEqual;
            };

            $scope.go = function(tab) {
                  if (!currentStateEqualTo(tab) && !tab.disable) {
                      $state.go(tab.route);
                  }
            };

            /* whether to highlight given route as part of the current state */
            $scope.is_active = function(tab) {

                var isAncestorOfCurrentRoute = $state.includes(tab.route, tab.params, tab.options);
                return isAncestorOfCurrentRoute;
            };

            $scope.update_tabs = function() {

                // sets which tab is active (used for highlighting)
                angular.forEach($scope.tabs, function(tab, index) {
                    tab.params = tab.params || {};
                    tab.options = tab.options || {};
                    tab.class = tab.class || "";

                    tab.active = $scope.is_active(tab);
                    if (tab.active) {
                        $scope.tabs.active = index;
                    }
                });
            };

            $scope.update_tabs();
        }],

        templateUrl: "./directives/widgets/tabs/tabs.html",
        restrict: "E"
    };
}]);

