/**
 * @ngdoc factories
 * @name keycloak
 * @memberof nrcomInspinia
 * @description
 * factory keycloak, ne fait que retourner keycloak
 *
 */
angular.module("nrcomInspinia")
.factory("keycloak",[
"$window","$log", "$state",
function($window,$log,$state){
    var keycloak = $window._keycloak,
    me = this;

    /**
     * Parse une chaine de droit en [ ressouce : droit ]
     * @method
     * @param  {string} string chaine de droit
     * @return {array}
     */
    me.stringToRoleArray = function(string){
        rolesarray = string.split(":");
        if (rolesarray.length === 1) {
            rolesarray = ["realm",rolesarray[0]];
        }
        return rolesarray;
    };

    /**
     * Retourne la verif du droit du user a partir d'une chaine
     * @method
     * @param  {string} roles La chaine de droit (ressource:droit)
     * @return {booleen}
     */
    keycloak.checkRolesString = function(roles){
        rolesarray = me.stringToRoleArray(roles);
        if(rolesarray[0] === "realm" ){
            return keycloak.hasRealmRole(rolesarray[1]);
        }else {
            return keycloak.hasResourceRole(rolesarray[1],rolesarray[0]);
        }
    };

    /**
     * Retourne la verif du droit du user a partir d'une chaine
     * @method
     * @param  {string} roles La chaine de droit (ressource:droit)
     * @return {booleen}
     */
    keycloak.checkRolesObject = function(roles){
        rolesArray = [];
        for (var role in roles) {
            if (roles.hasOwnProperty(role)) {
                if(angular.isString(roles[role])){
                    // mode ressource : valeur
                    rolesArray.push(role+":"+roles[role]);
                }else if(angular.isArray(roles[role])) {
                    // mode ressource : [ valeur1, valeur2 ]
                    for (var i = 0; i < roles[role].length; i++) {
                        rolesArray.push(role+":"+roles[role][i]);
                    }
                }else {
                    $log.warn("bad keycloak roles check structure",roles);
                    return false;
                }
            }
        }
        return keycloak.checkRoles(rolesArray);
    };

    /**
     * Verifie les roles de la persone
     * @method
     * @param  {mixed} roles roles de la personne
     * @return {boolean}
     */
    keycloak.checkRoles = function(roles){
        // Si pas de roles, c'est bon on peut passer
        if(!roles){
            return true;
        }

        if(angular.isString(roles)){
            // Si c'est une chaine on la traite comme une chaine
            return keycloak.checkRolesString(roles);
        }else if(angular.isArray(roles)) {
            // Si c'est un tableau, on réapel le fonction pour chaque elements
            return roles.every(function(value){return keycloak.checkRoles(value); });
        }else if(angular.isObject(roles)){
            // Si c'est un objet on l'envoie a une methode spé
            return keycloak.checkRolesObject(roles);
        }else {
            $log.warn("bad keycloak roles check structure",roles);
            return false;
        }
    };

    /**
     * A partir d'un nom d'etat, Verffie si l'utilisateur a le droit d'acces à
     * cet etat, et a tout ses parents.
     * @method
     * @param  {string} stateName nom de l'etat a tester
     * @return {boolean}
     */
    keycloak.checkStateStack = function(stateName){
        // On split le nom de l'entrée pour avoir la stack complete
        var statesWord = stateName.split(".");

        // premier etat, le root
        var state = "";
        var statesList = [ state ];

        // Pour chaque mot d'etat on ajoute son nom affin de construire une
        // liste avec tout les etats du root jusque au final
        for (var i = 0; i < statesWord.length; i++) {
            if(i>0){
                state += ".";
            }
            state += statesWord[i];
            statesList.push( state ) ;
        }

        // pour chaque etats, on récupere ses roles
        roles = statesList.map(function(stateName){
            return $state.get(stateName).roles;
        });
        // on check les roles
        return keycloak.checkRoles(roles);

    };

    return keycloak;
}]);

angular.module("nrcomInspinia")
.factory("swal",[
"$q",
function($q){
    var swalFactory={};

    swalFactory.defaults = {
        confirmDelete : {
            title: "Etes vous certain?",
            text: "La donnée supprimée ne poura pas etre réstaurée.",
            type: "warning",
            showCancelButton: true,
            confirmButtonColor: "#DD6B55",
            confirmButtonText: "Supprimer",
            cancelButtonText: "Annuler",
            closeOnConfirm: false,
            closeOnCancel: false,
            html: false,

            supressedTitle:"Supprimé !",
            supressedText:" ",
            supressedType:"success",

            canceledTitle:"Supression annulée !",
            canceledText:" ",
            canceledType:"info",

        }
    };


    /**
     * ouvre une alert
     * utilisation :
     * swalFactory.swal( "Titre" , "Message", "Type" )
     * @return {Promise}
     */
    swalFactory.swal = function(arg1, arg2, arg3) {
        var swalObject;
        if(angular.isString(arg1)){
            swalObject = {
                title:arg1,
                text:arg2,
                type:arg3
            };
        }else {
            swalObject = arg1;
        }

        return swalFactory.swalPromise( swalObject );
    };

    /**
     * Prend un swalObject l'execute et retourne une promise
     * @param  {Object} swalObject Objet sweetalert
     * @return {Promise}
     */
    swalFactory.swalPromise = function(swalObject){
        return $q(function(resolve,reject){
            swal(swalObject,function(result){
                if (result) {
                    resolve(result);
                }else {
                    reject(result);
                }
            });
        });
    };


    swalFactory.warning = function( title, message){
        return swalFactory.swal(title, message, "warning");
    };

    swalFactory.error = function( title, message){
        return swalFactory.swal(title, message, "error");
    };

    swalFactory.success = function( title, message){
        return swalFactory.swal(title, message, "success");
    };

    swalFactory.info = function( title, message){
        return swalFactory.swal(title, message, "info");
    };

    /**
     * Demande une confirmation pour delete
     * @param  {object} configdelete objet de configuration  ( cf les defaults )
     * @return {Promise}
     */
    swalFactory.confirmDelete=function(configdelete){
        var config = {};

        angular.extend(config,swalFactory.defaults.confirmDelete,configdelete);


        return $q(function(resolve,reject){
            swalFactory.swal(config)
            .then(function(){
                swalFactory.swal(config.supressedTitle,config.supressedText,config.supressedType).then((resolve));
            }).catch(function(){
                swalFactory.swal(config.canceledTitle,config.canceledText,config.canceledType).then((reject));
            });

        });

    };



    return swalFactory;
}]);

