angular.module('nrcomInspinia')
.factory('appResource', [ '$http', '$q', function($http, $q) {


    var applyParams = function(url, params) {

        var params = params || {},
        matches = url.match(/\:\w+/g);

        if(matches) {
            for(var i = matches.length - 1; i >= 0; i--) {
                var elt = matches[i].substr(1);
                if(params[elt]) {
                    url = url.replace(':' + elt, params[elt]);
                }
            }
        }

        return url;
    }

    var buildRequest = function(baseUrl, method, uri,transformRequest, headers,showTotal) {
        return function(item, arg) {
            return $q(function(resolve, reject) {
                $http({
                    data : item || {},
                    params : arg,
                    url : baseUrl + applyParams(uri, item),
                    method : method,
                    transformRequest : transformRequest,
                    headers : headers
                    /*transformRequest : angular.identify,
                    headers : {'Content-Type' : undefined}*/

                }).then(
                    function(result) {
                        if(result.data.success == false) {
                            reject(result.data.message);
                        } else {
                            if(showTotal){
                                resolve([result.data.data,result.data.total])
                            }else {
                                resolve(result.data.data);
                            }
                        }
                    },
                    function(err) {
                        reject(err);
                    }
                );
            });
        }
    };

    return function(configs) {

        var me = this,
        ret = {},
        methods = configs.methods || {},
        baseUrl = configs.baseUrl || '',
        transformRequest = configs.transformRequest || $http.defaults.transformRequest,
        headers = configs.headers || {'Content-Type' : 'application/json'};
        showTotal = configs.showTotal || false;

        for(var nameMethod in methods) {
            var config = methods[nameMethod];
            ret[nameMethod] = buildRequest(baseUrl, config.method, config.uri, transformRequest,headers,showTotal);
        };

        return ret;
    }

}]);

angular.module("nrcomInspinia")
.factory("appResourceProxy", [
"appResource", "globals",
function(appResource,globals) {
    var methodsConf = {
        "search" : {
            method : "GET",
            uri : "/search"
        },
        "get":{
            method : "GET",
            uri : ""
        },
        "getById":{
            method : "GET",
            uri : "/:_id"
        },
        "put":{
            method : "PUT",
            uri : "/:_id"
        },
        "post":{
            method : "POST",
            uri : ""
        },
        "delete" : {
            method : "DELETE",
            uri : "/:_id"
        }
    };
    return function(basePath,methods,config){
        if(!methods){
            methods = [
                "search",
                "get",
                "getById",
                "put",
                "post",
                "delete"
            ];
        }

        if(!config){
            config = {};
        }

        if(!config.methods){
            config.methods = {};
        }

        config.baseUrl = globals.plApi.baseUrl;

        for (var i = 0; i < methods.length; i++) {
            config.methods[methods[i]] = angular.copy(methodsConf[methods[i]]) ;
            config.methods[methods[i]].uri = basePath+config.methods[methods[i]].uri
        }

        service = appResource(config);
        service.methods = methods;
        service.config = config;


        return service;
    };
}]);

angular.module("nrcomInspinia")
