/**
 * @ngdoc providers
 * @name BreadcrumbsProvider
 * @alias mreadcrumbsProvider
 * @memberof nrcomInspinia
 * @description
 * Provider de breadcrumbs
 *
 * provider de breadcrumbs qui permet d'enregistrer les éléments
 * du breadcrumb en meme temps qu'on enregirstre son etat
 * @example
 *
 * // utilisation du provider pour ajouter une entrée en meme temps qu'un etat:
 *
 * angular.module("testBreadcrumbProvider")
 * .config(["$stateProvider", function(stateProvider){
 * $stateProvider.state("testBreadcrumb.state.subState",{
 *     url: "/subState",
 *     templateUrl: "./modules/state/subState/subState.html",
 *     entryName: "subState"
 * });
 *
 * // Nom d'entrée en fonction d'une donnée resolved:
 *
 * angular.module("testBreadcrumbProvider")
 * .config(["$stateProvider", function(stateProvider){
 * $stateProvider.state("testBreadcrumb.state.subState",{
 *     url: "/subState",
 *     templateUrl: "./modules/state/subState/subState.html",
 *     entryName: [ "cabinet", function(cabinet){ // On récupere la donnée cabinet des resolved via l'injecteur
 *          return cabinet.name;
 *     }],
 *     resove : {
 *          cabinet : function(){
 *              return {
 *                  name : "Fiducial 36"
 *              }
 *          }
 *     }
 * });
 *
 */
angular.module("nrcomInspinia")
.provider("breadcrumbs", function BreadcrumbsProvider() {
    "use strict";


    /**
     * Fonction qui provide le service
     * @memberof BreadcrumbsProvider
     * @method $get
     * @return {nrcomInspinia.breadcrumbsService}      service de breadcrumbs
     */
    this.$get = [
        "$rootScope",
        "$state",
        "$transitions",
        /**
         * @ngdoc serviceske

         * @name breadcrumbsService
         * @alias breadcrumbs
         * @memberof nrcomInspinia
         * @description
         * Service de gestion du menu
         */
        function breadcrumbsService($rootScope, $state, $transitions) {
            var breadcrumbs = [];
            var resolved = {};

            /**
             * Cette fonction permet de rafraichir un noeud du breadcrumb en lui
             * permetant de donner une fonction récupérant les données resolved
             *
             * @method refreshWithResolve
             * @param  {Object}           node Un noeud au sens de noeud de transition
             * @return {Object}                Un noeud
             */
            function refreshNodeWithResolve(node){
                var custom,
                    state = $state.get(node.state.name);

                // on update les resolved avec l'etat courant
                var current;
                for (var i = 0; i < node.resolvables.length; i++) {
                    current = node.resolvables[i];
                    resolved[current.token] = current;
                }

                // Si entryName est un tableau, on le résoud comme l'injecteur angular
                if( angular.isArray(state.entryName)){


                    // On douplique l'entryname
                    var args = state.entryName.slice(0);

                    // la fonction est le dernier argupment
                    var entryNameFn = args.pop();

                    // on apply la fonction avec les resolved des autres parametres
                    custom = entryNameFn.apply(
                        null,
                        args.map(function(token){
                            if(resolved[token]){
                                return resolved[token].data;
                            }else {
                                return null;
                            }
                        })
                    );

                }

                return {
                    text: custom ? custom : state.entryName,
                    stateName: state.redirect ? state.redirect : state.name 
                };
            }

            /**
             * Rafraichi le breadcrumbs avec un objet transition
             * @method refreshBreadcrumb
             * @param  {Object}          trans Transition ui-router
             * @return {void}
             */
            function refreshBreadcrumb(trans) {

                // Récupération de l'abre de transition
                var toPathNodes = trans.treeChanges().to;

                resolved = {};

                // On réset le tableau ( pas de [] sinon on perd le pointeur )
                breadcrumbs.length = 0;

                // On aply au breadcrumbs ( donc le poiteur du tablkeau)
                // La fonction de refresh qui permet de resolve
                Array.prototype.push.apply(
                    breadcrumbs,
                    toPathNodes.map(refreshNodeWithResolve).filter(angular.identity)
                );
            }

            $transitions.onSuccess( {}, refreshBreadcrumb );

            return breadcrumbs;
        }
    ];
});

/**
 * @ngdoc providers
 * @name MenuProvider
 * @alias menuProvider
 * @memberof nrcomInspinia
 * @description
 * Provider de menu
 *
 * provider de menu qui permet de configurer les factory de menu lors de la config.
 * Le provider vous permet d"ajouter une entrée a la configuration, la factory vous
 * permet d"utiliser les entrées menues lors de l'execution
 * examples d"utilisations :
 *
 * @example <caption>Ajout d'une entrée simple</caption>
 * // utilisation du provider pour ajouter une entrée menu :
 *
 * angular.module("testMenuProvider")
 * .config(["menuProvider", function(menuProvider){
 *      menuProvider.add({
 *          stateName : "index.accueil"     // nom de l'etat dans stateProvider
 *          name : "Accueil"                // nom de l'entrée
 *          icon : "home"                   // nom de l'icone font-awesome
 *          importance : 1000               // importance de l'entrée. (pour ordonée)
 *      });
 * }]);
 *
 * @example <caption>Ajout d'un ellement avec un sous-niveau</caption>
 *
 * menuProvider.add({
 *      stateName : "testindex.index",
 *      icon : "home",
 *      name : "accueil"
 *      children : [
 *          stateName : "testindex.item.subItem",
 *          icon : "home",
 *          name : "subItem"
 *      ]
 * });
 *
 * @example <caption>Ajout d'un element avec un badge </caption>
 * menuProvider.add({
 *      stateName : "index.accueil"     // nom de l'etat dans stateProvider
 *      name : "Accueil"                // nom de l'entrée
 *      icon : "home"                   // nom de l'icone font-awesome
 *      badge : {
 *          text : "18",
 *          type : "danger"
 *      }
 * });
 */
angular.module("nrcomInspinia")
.provider("menu", [function MenuProvider(){

    var _items = [];


    /**
     * Fonction qui provide le service
     * @memberof MenuProvider
     * @method $get
     * @return {nrcomInspinia.menuService}      service de menu
     */
    this.$get = [ "$state" , "$log" ,
    /**
     * @ngdoc services
     * @name menuService
     * @alias menu
     * @memberof nrcomInspinia
     * @description
     * Service singleton de gestion du menu
     * exemple d'utilisations :
     * @example
     * // utilisation du service pour récupérer les entrées menues :
     * angular.module("testMenuProvider")
     * .controller("mainController",["menu",function(menu){
     *      var menuItems = menu.getItems();
     *      // faites quelque chose avec menuItems;
     * }]);
     */
    function menuService($state, $log){
        return {

            /**
             * Permet de récupérer les items du menu
             *
             * @memberof menuService
             * @method getItems
             * @return {Array} items du menu
             */
            getItems: function() {
                return _items;
            },

            /**
             * Permet de récupérer les items du menu correctements enregistrés
             * dans le stateProvider
             *
             * @memberof menuService
             * @method getRegistredItems
             * @return {Array} items du menu presents dans $state
             */
            getRegistredItems: function() {
                var registredItems = [];
                for (var i = 0; i < _items.length; i++) {
                    if($state.get(_items[i].stateName)){
                        registredItems.push(_items[i]);
                    }
                }
                return registredItems;
            },

            /**
             * Verifie la presence des items du menu dans $state et retourne ceux
             * qui n"y sont pas
             *
             * @memberof menuService
             * @method checkItems
             * @param  {boolean} verbose Ajoute un message
             * @return {Array}         items non-presents dans $state
             */
            checkItems: function(verbose){
                var unRegistredItems = [];
                for (var i = 0; i < _items.length; i++) {
                    if(!$state.get(_items[i].stateName)){
                        if(verbose){
                            $log.warn("[menu-service]: The item \""+_items[i].name+"\" with state name \""+_items[i].stateName+"\" isn't registred in state provider");
                        }
                        unRegistredItems.push(_items[i]);
                    }
                }
                return unRegistredItems;
            }
        };
    }];

    /**
     * Permet d"ajouter un item au menu
     * strucutre :
     * @example
     * {
     *      stateName : "nom.etat",
     *      name : "Mon etat",
     *      icon : "font-awesome-icon"
     * }
     * @memberof MenuProvider
     * @method add
     * @param  {Object} item un objet de menu
     * @return {nrcomInspinia.MenuProvider}      this
     */
    this.add = function(item) {
        _items.push(item);
        return this;
    };
}]);

